import React, { Component } from "react";
class Counter extends Component {
  state = { count: 1 };
  render() {
    return (
      <div>
        <span className="badge badge-primary m-2">
          {this.formatCount()}
          <button>Increment</button>
        </span>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
