  state = { count: 0, tags: [] };

  renderTags() {
    if (this.state.tags.length === 0) return <p>There are no tags !</p>;
    return (
      <ul>
        {this.state.tags.map((tag) => (
          <li key={tag}>{tag}</li> // cara merender list item diReact
        ))}
      </ul>
    );
  }

