import React, { Component } from "react";
import Counter from "./counter";
class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 4 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };

  handleDelete = () => {
    // Event handler ini akan mempassing reference
    // melalui props ke child component yaitu counter.jsx
    console.log("Event Handler Called");
    // ini adalah event handler untuk mengubah (menghapus) array pada state
    // STATE HANYA DAPAT DIUBAH didalam komponen tempat state itu berada
  };
  render() {
    return (
      <div>
        {this.state.counters.map((counter) => (
          <Counter
            key={counter.id}
            onDelete={this.handleDelete}
            value={counter.value}
          >
            <h4>Counter # {counter.id}</h4>
          </Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
